import classes from './Post.module.css'
import close_btn_img from '../modal/modal_img/close.svg'
import $ from 'jquery'

const Post = (props) => {

    props.postModalActive ? $('body').css('overflow', 'hidden') : $('body').css('overflow', 'overlay');

    const close_btn = (
        <div className="close_btn">
            <div>
                <img onClick={() => props.setModalPostActive(false)} draggable="false" id="close_img" src={close_btn_img} alt="" />
            </div>
        </div>
    )

    props.post = props.post.filter((n) => {return n !== ''});
    props.post = props.post.filter((n) => {return n !== ' '});

    return (
            <div className={props.postModalActive ? classes.post_active : classes.post} onClick={props}>
                <div className={classes.post_content} onClick={e => e.stopPropagation()}>
                    {close_btn}
                    <div className={classes.post_content_container}>
                        {prpost.map((e, index) => (
                            <p key={index} >{e}</p>
                        ))}

                    </div>
                </div>
            </div>
    )
}

export default Post;
