import classes from './Post.module.css'
import close_btn_img from '../modal/modal_img/close.svg'
import $ from 'jquery'
import Modal from '../modal/Modal'

const Post = ({ postModalActive, setModalPostActive, num }) => {

    postModalActive ? $('body').css('overflow', 'hidden') : $('body').css('overflow', 'overlay');

    const close_btn = (
        <div className="close_btn">
            <div>
                <img onClick={() => setModalPostActive(false)} draggable="false" id="close_img" src={close_btn_img} alt="" />
            </div>
        </div>
    )


    return (
        <Modal num={num} active={postModalActive} setActive={setModalPostActive} />
    )
}

export default Post;
