import classes from './Post.module.css'
import close_btn_img from '../modal/modal_img/close.svg'

const Post = ({post, postModalActive, setModalPostActive }) => {

    pos ? $('body').css('overflow', 'hidden') : $('body').css('overflow', 'overlay');

    const close_btn = (
        <div className="close_btn">
            <div>
                <img onClick={() => setModalPostActive(false)} draggable="false" id="close_img" src={close_btn_img} alt="" />
            </div>
        </div>
    )


    return (
        <div className={postModalActive ? classes.post_active : classes.post} onClick={() => setModalPostActive(false)}>
            <div className={classes.post_content} onClick={e => e.stopPropagation()}>
                {close_btn}
                <p>{post}</p>
            </div>
        </div>
    )
}

export default Post;
