import React, { useState, useEffect } from "react";
import Post from "../post/Post";
import classes from './FeedPost.module.css'
import $ from 'jquery'



const FeedPost = ({ post_num, post, post_img }) => {

    const [modalPostActive, setModalPostActive] = useState(false);

    let feed_post = post.slice(0, 3);

    useEffect(() => post_img === null ? $('#feed_element_container' + post_num).css('color', 'black') : $('#feed_element_container' + post_num).css('color', 'white')
    , [post_num, post_img]
    )
    return (
        <div onClick={() => setModalPostActive(true)} style={{ backgroundImage: 'url(' + post_img + ')', backgroundRepeat: 'no-repeat', backgroundSize: 'cover', opacity: "0.1" }} id={'feed_element_container' + post_num} className={classes.feed_element_container}>
            <div className={classes.feed_element_header}>
                <p>Новость №{post_num + 1}</p>
            </div>
            <div id={"feed_element_content" + post_num} className={classes.feed_element_content}>
                {feed_post.map((e, index) => (
                    <p key={index} >{e}</p>
                ))}
                {/* <img onClick={() => setModalPostActive(false)} width="300px" src={post_img} alt="" /> */}

            </div>
            <Post postModalActive={modalPostActive} setModalPostActive={setModalPostActive} num={post_num + 4} post={post} />
        </div>
    )
}

export default FeedPost;
