import React from "react";
import FeedPost from "./FeedPost";
import classes from './FeedContainer.module.css'


const url = "http://127.0.0.1:5000"

// Пример отправки POST запроса:
async function postData(url = '', data = {}) {
    // Default options are marked with *
    const response = await fetch(url, {
      method: 'POST', // *GET, POST, PUT, DELETE, etc.
      mode: 'cors', // no-cors, *cors, same-origin
      cache: 'no-cache', // *default, no-cache, reload, force-cache, only-if-cached
      credentials: 'same-origin', // include, *same-origin, omit
      headers: {
        'Content-Type': 'application/json'
        // 'Content-Type': 'application/x-www-form-urlencoded',
      },
      redirect: 'follow', // manual, *follow, error
      referrerPolicy: 'no-referrer', // no-referrer, *client
      body: JSON.stringify(data) // body data type must match "Content-Type" header
    });
    return await response.json(); // parses JSON response into native JavaScript objects
  }

  postData('https://example.com/answer', { answer: 42 })
    .then((data) => {
      console.log(data); // JSON data parsed by `response.json()` call
    });



let response = null;
try {
    response = postDa();
}
catch {
    console.log('Server requeet error')
}

const renderFeedContainerContent = (response) => {
    if (response != null) {
        return (
            <div className={classes.feed_container_content}>
                {response['posts'].map((e, index) => (
                    <FeedPost key={index} post={e} post_img={response['img'][index]} post_num={response['posts'].indexOf(e)} />
                ))}
            </div>)
    }
    else {
        return (
            <div className={classes.feed_container_content}>
                <p>SERVER RESPONSE ERROR</p>
            </div>)
    }
}

const FeedContainer = () => {


    return (
        <div className={classes.feed_container}>
            <div className={classes.feed_container_header}>
                <p>ПОСЛЕДНИЕ НОВОСТЕЙ</p>
            </div>
            {renderFeedContainerContent(response)}
        </div>
    )
}

export default FeedContainer;
