import React from "react";
import FeedPost from "./FeedPost";
import classes from './FeedContainer.module.css'
import { useState, useEffect } from "react";


const url = "http://127.0.0.1:5000/api/"


const renderFeedContainerContent = (response) => {

    if (response !== null && response !== Promise) {
        return (
            <div className={classes.feed_container_content}>
                {response['posts'].map((e, index) => (
                    <FeedPost key={index} post={e} post_img={response['img'][index]} post_num={response['posts'].indexOf(e)} />
                ))}
            </div>)
    }
    else {
        return (
            <div className={classes.feed_container_content}>
                <p>SERVER RESPONSE ERROR</p>
            </div>)
    }
}

const FeedContainer = () => {

    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [items, setItems] = useState([]);

    let response = null;

    useEffect(() => {
        fetch("https://api.example.com/items")
          .then(res => res.json())
          .then(
            (result) => {
              setIsLoaded(true);
              setItems(result);
            },
            // Note: it's important to handle errors here
            // instead of a catch() block so that we don't swallow
            // exceptions from actual bugs in components.
            (error) => {
              setIsLoaded(true);
              setError(error);
            }
          )
      }, [])

      if (error) {
        return <div>Error: {error.message}</div>;
      } else if (!isLoaded) {
        return <div>Loading...</div>;
      } else {
        return (
          <ul>
            {items.map(item => (
              <li key={item.id}>
                {item.name} {item.price}
              </li>
            ))}
          </ul>
        );
      }


    return (
        <div className={classes.feed_container}>
            <div className={classes.feed_container_header}>
                <p>ПОСЛЕДНИЕ НОВОСТЕЙ</p>
            </div>
            {renderFeedContainerContent(response)}
        </div>
    )
}

export default FeedContainer;
