import React  from 'react';
import Modal from 'react-bootstrap/Modal';
import '..'

const Post = ({ post_img, post, postModalActive, setPostModalActive, setShow, show }) => {


    post = post.filter((n) => { return n !== '' });
    post = post.filter((n) => { return n !== ' ' });


    return (
        <>
            <Modal
                show={show}
                onHide={() => setShow(false)}
                dialogClassName="modal-80w"
                aria-labelledby="example-custom-modal-styling-title"
                size='lg'
            >
                <Modal.Header closeButton te>
                    <Modal.Title id="example-custom-modal-styling-title">
                        {post[0]}
                    </Modal.Title>
                </Modal.Header>
                <Modal.Body size='lg'>
                    {post.map((e, index) => (
                        <p key={index} >{e}</p>
                    ))}
                    <img width="300px" src={post_img} alt="" />
                </Modal.Body>
            </Modal>
        </>
    )
}

export default Post;
