import React from 'react'
import classes from './pages.module.css'
import { useState, useEffect } from 'react';

const Schedule = () => {

    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [items, setItems] = useState([]);

    const url = "http://127.0.0/api/schedule"

    try {
        useEffect(() => {
            fetch(url)
                .then(res => res.json())
                .then(
                    (result) => {
                        setIsLoaded(true);
                        setItems(result);
                    },
                    (error) => {
                        setIsLoaded(true);
                        setError(error);
                    }
                )
        }, [])
    }
    catch {
        console.log("SERVER REQUEST ERROR")
    }

    if (error) {
        return (
            <div className={classes.feed_container}>
                <div>ERROR: {error.message}</div>
            </div>)
    } else if (!isLoaded) {
        return (
            <div className={classes.feed_container}>
                <div>LOADING...</div>
            </div>)
    } else {
        return (
        <div className="container">
            <div className={classes.page_content}>
                <div id="page_window2" className={classes.page_window}>
                    <div id="page_window_text2" className={classes.page_window_text}>
                        {console.log(items['board'].split('\n'))}
                        {items['board'].split('\n').map(element => 
                            <p key={element}>{element}</p>
                        )}
                    </div>
                </div>
            </div>
        </div>
        );
    }

}
