import React from "react";
import FeedPost from "./FeedPost";
import classes from './FeedContainer.module.css'
import $ from 'jquery'



$.ajaxSetup({ // тип запроса
    type: "POST",
    data: {},
    dataType: 'json',
    xhrFields: {
        withCredentials: true
    },
    crossDomain: true,
    contentType: 'application/json; charset=utf-8'
});


function sendJSON(user_data, user_url, req_type) { // запрос на сервер
    let xhr = new XMLHttpRequest();
    let url = "http://127.0.0.1:5000" + user_url;
    xhr.open(req_type, url, false);
    xhr.setRequestHeader("Content-Type", "application/json");
    xhr.onreadystatechange = function () {
        if (xhr.readyState === 4 && xhr.status === 200) {
            console.log(req_type + ' request was received');
        }
    };
    xhr.send(JSON.stringify(user_data));
    return JSON.parse(xhr.responseText);
}

try {
    response = sendJSON('', '/api/v1/', 'GET');
}
catch {
    console.log('Server requeet error')
}

const FeedContainer = ({}) => {


    return (
        <div className={classes.feed_container}>
            <div className={classes.feed_container_header}>
                <p>ПОСЛЕДНИЕ НОВОСТЕЙ</p>
            </div>
            <div className={classes.feed_container_content}>
                {response['posts'].map((e, index) => (
                    <FeedPost key={index} post={e} post_img={response['img'][index]} post_num={response['posts'].indexOf(e)} />
                ))}
            </div>
        </div>
    )
}

export default FeedContainer;
