import React, { useState, useEffect } from 'react';
import Carousel from 'react-bootstrap/Carousel';
import './ControlledCarousel.css'

function ControlledCarousel() {
    const [index, setIndex] = useState(0);

    const handleSelect = (selectedIndex, e) => {
        setIndex(selectedIndex);
    };

    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [items, setItems] = useState([]);

    const url = "https://uralafonperm.ru/api/"

    try {
        useEffect(() => {
            fetch(url)
                .then(res => res.json())
                .then(
                    (result) => {
                        setIsLoaded(true);
                        setItems(result);
                    },
                    (error) => {
                        setIsLoaded(true);
                        setError(error);
                    }
                )
        }, [])
    }
    catch {
        console.log("SERVER REQUEST ERROR")
    }

    if (error) {
        return (
            <div >

            </div>)
    } else if (!isLoaded) {
        return (
            <div >
            </div>)
    } else {

        let img_list = items['img']
        img_list = img_list.filter((n) => { return n !== null });
        return (

            <Carousel style={{ height: '300px', width: '500px' }} variant='dark' className='carousel' activeIndex={index} onSelect={handleSelect}>
                {img_list.map((e, index) => (
                    <Carousel.Item key={index}>
                        <img  src={e} alt="" />
                    </Carousel.Item>
                ))}
            </Carousel>
        );
    }

}

export default ControlledCarousel;
