import React, { useState, useEffect } from "react";
import Post from "../post/Post";
import classes from './FeedPost.module.css'
import $ from 'jquery'


const FeedPost = ({ post_num, post, post_img }) => {

    const [modalPostActive, setModalPostActive] = useState(false);


    let feed_post = post.slice(0, 3);

    feed_post = feed_post.filter((n) => {return n !== ' '});

    const styleFeed = () => {
        if (post_img === null)
            $('#feed_element_container' + post_num).css('color', 'black')
        else {
            $('#feed_element_container' + post_num).css('color', 'white')
            $('#feed_element_content' + post_num).css('background-color', 'rgba(0, 0, 0, .2)')
        }
    }

    useEffect(styleFeed, [post_num, post_img])


    return (
        <div onClick={() => setModalPostActive(true)} style={{ backgroundImage: 'url(' + post_img + ')', backgroundRepeat: 'no-repeat', backgroundSize: 'cover' }} id={'feed_element_container' + post_num} className={classes.feed_element_container}>
            <div className={classes.feed_element_header}>
                <p>Новость №{post_num + 1}</p>
            </div>
            <div id={"feed_element_content" + post_num} className={classes.feed_element_content}>
                {feed_post.map((e, index) => (
                    <p id={"feed_element_content_text"+post_num} key={index} >{e}</p>
                ))}
                {/* <img onClick={() => setModalPostActive(false)} width="300px" src={post_img} alt="" /> */}

            </div>
            <Post postModalActive={modalPostActive} setPostModalActive={setModalPostActive} post={post} />
        </div>
    )
}

export default FeedPost;
