            <div style={{ paddingTop: '2rem', paddingBottom: '2rem', display:f }}
                onClick={() => setModalPostActive(true)}
                id={'feed_element_container' + post_num}
                className={'feed_element_container'}>
                <Row>
                    <Col>
                        <Card style={{ width: '18rem' }}>
                            <Card.Img variant="top" src={post_img} style={{height:'140px'}}/>
                            <Card.Body>
                                <Card.Title style={{height:'140px', overflow:"hidden"}}>
                                    {feed_post.map((e, index) => (
                                        <p className={classes.feed_element_content_text} id={"feed_element_content_text" + post_num} key={index} >{e}</p>
                                    ))}</Card.Title>
                            </Card.Body>
                            <Button variant="primary">Learn More</Button>
                        </Card>
                    </Col>
                </Row>
            </div>
            <Post post_img={post_img} postModalActive={modalPostActive} setPostModalActive={setModalPostActive} post={post} />
        </>
    )
}

export default FeedPost;
