import classes from './Post.module.css'
import close_btn_img from '../../img/close.svg'
import $ from 'jquery'
import { Container, Col, Row, Card, Button } from "react-bootstrap";

const Post = ({ post_img, post, postModalActive, setPostModalActive }) => {

    postModalActive ? $('body').css('overflow', 'hidden') : $('body').css('overflow', 'overlay');

    const close_btn = (
        <div className="close_btn">
            <div>
                <img onClick={() => setPostModalActive(false)} draggable="false" id="close_img" src={close_btn_img} alt="" />
            </div>
        </div>
    )

    post = post.filter((n) => { return n !== '' });
    post = post.filter((n) => { return n !== ' ' });


    return (
        <><div>
            <div className={postModalActive ? classes.post_active : classes.post} onClick={() => setTimeout(() => setPostModalActive(false), 0)}>
                <div className={classes.post_content} onClick={e => e.stopPropagation()}>
                    {close_btn}
                    <div className={classes.post_content_container}>
                        {post.map((e, index) => (
                            <p key={index} >{e}</p>
                        ))}
                        <img width="300px" src={post_img} alt="" />
                    </div>
                </div>
            </div>
        </div>
        <Container style={{ paddingTop: '2rem', paddingBottom: '2rem' }}>
            <Row>
                <Col>
                    <Card style={{ width: '18rem' }}>
                        <Card.Img />
                        <Card.Body>
                            <Card.Title>Title Text</Card.Title>
                            <Card.Text>Main Text</Card.Text>
                        </Card.Body>
                        <Button variant="primary">Learn More</Button>
                    </Card>
                </Col>
            </Row>
        </Container></>
    )
}

export default Post;
