import React from "react";
import classes from './Header.module.css'
import { Container, Nav, Navbar } from 'react-bootstrap'

import { BrowserRouter as Router, Route, Routes } from 'react-router-dom';
import About from '../../pages/About';
import Schedule from '../../pages/Schedule';
import Office from '../../pages/Office';
import Donations from '../../pages/Donations';
import Main from '../../pages/Main'
import $ from 'jquery'

import { useState } from "react";


const Header = () => {

    const [modalActive, setModalActive] = useState(false)
    const [btnNumClick, setbtnNumClick] = useState(0);

    modalActive ? $('body').css('overflow', 'hidden') : $('body').css('overflow', 'overlay');

    return (
        <>
            <Navbar collapseOnSelect expand="md" bg="dark" variant="dark">
                <Container>
                    <div href="/" className={classes.header_logo}>
                        <p className={classes.header_logo_text}>Белогорский монастырь</p>
                    </div>
                    <Navbar.Toggle aria-controls="responsive_navbar_nav" />
                    <Navbar.Collapse className={classes.responsive_navbar_nav}>
                        <Nav className="mr-auto">
                            <Nav.Link href="/">Главная</Nav.Link>
                            <Nav.Link href="/about">История</Nav.Link>
                            <Nav.Link href="/schedule">Расписание</Nav.Link>
                            <Nav.Link href="/office">Подворье</Nav.Link>
                            <Nav.Link href="/donations">Пожертвования</Nav.Link>
                        </Nav>
                    </Navbar.Collapse>
                </Container>
            </Navbar>
            <Router>
                <Routes>
                    <Route exact path='/' element={<Main/>}/>
                    <Route exact path='/about' element={<About btnNum={btnNumClick} setbtnNum={setbtnNumClick} active={modalActive} setActive={setModalActive} />} />
                    <Route exact path='/schedule' element={<Schedule />} />
                    <Route exact path='/office' element={<Office />} />
                    <Route exact path='/donations' element={<Donations />} />
                </Routes>
            </Router>
        </>
    )
}

export default Header;
