import React, { useState } from "react";
import classes from './Post.module.css'
import close_btn_img from '../modal_i'

const Post = ({ post_num, post, postModalActive, setModalPostActive }) => {

    const close_btn = (
        <div className="close_btn">
            <div>
                <img onClick={() => setModalPostActive(false)} draggable="false" id="close_img" src={close_btn_img} alt="" />
            </div>
        </div>
    )


    return (
        <div id={postModalActive ? "post_active" : 'post'} className={postModalActive ? classes.post_active : classes.post}>
            <div>
                {close_btn}
                <p>{post}</p>
            </div>
        </div>
    )
}

export default Post;
