import React, { useState, useEffect } from "react";
import Post from "../post/Post";
import classes from './FeedPost.module.css'
import { Row, Col, Button, Card } from 'react-bootstrap'


const FeedPost = ({ post_num, post, post_img }) => {

    const [modalPostActive, setModalPostActive] = useState(false);


    let feed_post = post.slice(0, 4);

    feed_post = feed_post.filter((n) => { return n !== ' ' });
    feed_post = feed_post.filter((n) => { return n !== '' });
    feed_post = feed_post.filter((n) => { return n !== '\n' });

    return (
        <>
            {/* <div className={classes.feed_element_header}>
                <p>Новость №{post_num + 1}</p>
            </div>
            <div id={"feed_element_content" + post_num} className={classes.feed_element_content}>
                <div>
                    {feed_post.map((e, index) => (
                        <p className={classes.feed_element_content_text} id={"feed_element_content_text" + post_num} key={index} >{e}</p>
                    ))}
                </div>
            </div> */}
            <Col>
            <div style={{ paddingTop: '2rem', paddingBottom: '2rem' }}
                onClick={() => setModalPostActive(true)}
                id={'feed_element_container' + post_num}
                className={'feed_element_container'}>

                    <Card style={{ width: '18rem' }}>
                        <Card.Img variant="top" src={post_img} style={{ height: '140px' }} />
                        <Card.Body>
                            <Card.Title style={{ height: '140px', overflow: "hidden" }}>
                                {feed_post.map((e, index) => (
                                    <p className={classes.feed_element_content_text} id={"feed_element_content_text" + post_num} key={index} >{e}</p>
                                ))}</Card.Title>
                        </Card.Body>
                        <Button variant="primary">Learn More</Button>
                    </Card>
            </div>
            <Post post_img={post_img} postModalActive={modalPostActive} setPostModalActive={setModalPostActive} post={post} />
            </Col>
        </>
    )
}

export default FeedPost;
