import React, { useState } from 'react';
import { Container } from 'react-bootstrap';
import Modal from 'react-bootstrap/Modal';
import './Post.css'
import { Lightbox } from "react-modal-image";


const Post = ({ post_num, post_img, post, setShow, show }) => {

    const [imgModalActive, setImgModalActive] = useState(false);

    post = post.filter((n) => { return n !== '' });
    post = post.filter((n) => { return n !== ' ' });





    return (
        <>
            <Modal
                show={show}
                onHide={() => setShow(false)}
                dialogClassName="modal-90w"
                aria-labelledby="example-custom-modal-styling-title"
                size='lg'
            >
                <Modal.Header closeButton>
                    <Container>
                        <Modal.Title id="example-custom-modal-styling-title">
                            Новость №{post_num + 1}
                        </Modal.Title>
                    </Container>
                </Modal.Header>
                <Modal.Body size='lg'>
                    <Container>
                        {post.map((e, index) => (
                            <p key={index} >{e}</p>
                        ))}
                        <ModalImage
  small={urlToTinyImageFile}
  large={urlToHugeImageFile}
  alt="Hello World!"
/>;
                        <img onClick={() => setImgModalActive(true)} width="300px" src={post_img} alt="" />
                    </Container>
                </Modal.Body>

            </Modal>
        </>
    )
}

export default Post;
