import React, { useState, useEffect } from 'react';
import { Container } from 'react-bootstrap';
import Carousel from 'react-bootstrap/Carousel';
import FeedContainer from '../feed/FeedContainer.jsx';

function ControlledCarousel() {
    const [index, setIndex] = useState(0);

    const handleSelect = (selectedIndex, e) => {
        setIndex(selectedIndex);
    };

    const [error, setError] = useState(null);
    const [isLoaded, setIsLoaded] = useState(false);
    const [items, setItems] = useState([]);

    const url = "https://uralafonperm.ru/api/"

    try {
        useEffect(() => {
            fetch(url)
                .then(res => res.json())
                .then(
                    (result) => {
                        setIsLoaded(true);
                        setItems(result);
                    },
                    (error) => {
                        setIsLoaded(true);
                        setError(error);
                    }
                )
        }, [])
    }
    catch {
        console.log("SERVER REQUEST ERROR")
    }

    if (error) {
        return (
            <div >

            </div>)
    } else if (!isLoaded) {
        return (
            <div >
            </div>)
    } else {

        let img_list = items['img']
        img_list = img_list.filter((n) => { return n !== null });
        return (
            <Container>
                <Carousel activeIndex={index} onSelect={handleSelect}>
                Cont
                    {img_list.map((e, index) => (
                        <Carousel.Item key={index}>
                            <img style={{ height: '600px', width: '80%' }} src={img_list[index]} alt=""></img>
                            <Carousel.Caption>
                                <h3>First slide label</h3>
                                <p>Nulla vitae elit libero, a pharetra augue mollis interdum.</p>
                            </Carousel.Caption>
                        </Carousel.Item>
                    ))}
                </Carousel>
            </Container>
        );
    }

}

export default ControlledCarousel;
