import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';


const root = ReactDOM.createRoot(document.getElementById('root'));
root.render(
    <App />
);

// лента новостей 

$.ajaxPrefilter(function( options, original_Options, jqXHR ) {
    options.async = true;
});

$.ajaxSetup({ // тип запроса
    type: "POST",
    data: {},
    dataType: 'json',
    xhrFields: {
        withCredentials: true
    },
    crossDomain: true,
    contentType: 'application/json; charset=utf-8'
});


function sendJSON(user_data, user_url, req_type) { // запрос на сервер
    let xhr = new XMLHttpRequest();
    let url = "http://127.0.0.1:5000" + user_url;
    xhr.open(req_type, url, false);
    xhr.setRequestHeader("Content-Type", "application/json");
    xhr.onreadystatechange = function () {
        if (xhr.readyState === 4 && xhr.status === 200) {
            console.log(req_type + ' request was received');
        }
    };
    xhr.send(JSON.stringify(user_data));
    return JSON.parse(xhr.responseText);
}


function parsePost(post, num) {
    let post_block = $(`<div id="feed_element_content${num}" class="feed_element_content"></div>`);
    $(`#feed_element_container${num}`).append(post_block);
    for (let i = 0; i < post['posts'][num].length; i++) {
        $(`#feed_element_content${num}`).append($(`<p>${post['posts'][num][i]}</p>`));  
    }
}


function addToFeed() {
    let response = sendJSON('', '/api/v1/', 'GET');
    for (let i = 0; i < response['posts'].length; i++) {
        $('.feed_container_content').append(`<div id="feed_element_container${i}" class="feed_element_container"><div class="feed_element_header"><p>Новость №${i+1}</p></div></div>`)
        parsePost(response, i)
    }
}
