import React, { useState } from 'react'
import FeedContainer from './components/feed/FeedContainer.jsx';
import Footer from './components/footer/Footer.jsx'
import Header from './components/header/Header.jsx'
import NavItem from './components/navigation/NavItem.jsx';
import VideoHeader from './components/header/VIdeoHeader.jsx';
import './styles/index.css'
import book_img from './img/book.svg'
import calendar_img from './img/calendar.svg'
import office_img from './img/office.svg'
import donations_img from './img/donations.svg'
import Modal from './components/modal/Modal.jsx'
import $ from 'jquery'



$.ajaxSetup({ // тип запроса
    type: "POST",
    data: {},
    dataType: 'json',
    xhrFields: {
        withCredentials: true
    },
    crossDomain: true,
    contentType: 'application/json; charset=utf-8'
});


function sendJSON(user_data, user_url, req_type) { // запрос на сервер
    let xhr = new XMLHttpRequest();
    let url = "http://127.0.0.1:5000" + user_url;
    xhr.open(req_type, url, false);
    xhr.setRequestHeader("Content-Type", "application/json");
    xhr.onreadystatechange = function () {
        if (xhr.readyState === 4 && xhr.status === 200) {
            console.log(req_type + ' request was received');
        }
    };
    xhr.send(JSON.stringify(user_data));
    return JSON.parse(xhr.responseText);
}

let response = sendJSON('', '/api/v1/', 'GET');

function App() {

    const [modalActive, setModalActive] = useState(false)
    const [btnNumClick, setbtnNumClick] = useState(0);

    modalActive ? $('html').css('overflow', 'hidden') : $('html').css('overflow', 'auto');

    return (
        <div className='App'>
            <VideoHeader />
            <Header />
            <main>
                <div className="container">
                    <div className="btn_window_container">
                    </div>
                    <div className="main_container">
                        <div className="nav">
                            <NavItem btn_name={"ИСТОРИЯ"} btnNum={btnNumClick} setBtnNum={setbtnNumClick} active={modalActive} setActive={setModalActive} nav_num={1} nav_img={book_img} />
                            <NavItem btn_name={"РАСПИСАНИЕ БОГОСЛУЖЕНИЙ"} btnNum={btnNumClick} setBtnNum={setbtnNumClick} active={modalActive} setActive={setModalActive} nav_num={2} nav_img={calendar_img} />
                            <NavItem btn_name={"ПОДВОРЬЕ"} btnNum={btnNumClick} setBtnNum={setbtnNumClick} active={modalActive} setActive={setModalActive} nav_num={3} nav_img={office_img} />
                            <NavItem btn_name={"ПОЖЕРТВОВАНИЯ"} btnNum={btnNumClick} setBtnNum={setbtnNumClick} active={modalActive} setActive={setModalActive} nav_num={4} nav_img={donations_img} />
                        </div>
                    </div>
                    <FeedContainer responseText={response} />
                </div>
            </main>
            <Modal num={btnNumClick} active={modalActive} setActive={setModalActive} />
            <Footer />
        </div>
    );
}

export default App;
